import { useEffect, useState } from 'react'
import apiService from '../APIservice'
import './profile.css'

function Profile (userID) {
    
    const [userData, changeUserData] = useState({})

    useEffect(() => {
        async function getUserInfo () {
            const resUserData = await apiService.getUser(userID);
            changeUserData(resUserData);
        }
        getUserInfo ()
    }, [userID])

    return (
        <div className="profile">
        <div className="picture-placeholder">

        </div>
        <div className="profile-info">
          <h2>{userData.username ? userData.username : "Username"}</h2>
          <p>City: {userData.city ? userData.city : "City"}</p>
          <p>State: {userData.state? userData.state.toUpperCase() : "State"}</p>
        </div>
      </div>
    )
}

export default Profile
