import { useEffect, useState } from 'react'
import './message-mini.css'

function MessageMini ({fromUser, toUser, album, offeredAlbum, message}) {

   const [albumInfo, changeAlbumInfo] = useState({})

  useEffect (()=> {
    async function getInfo () {
        const info =  await apiService.getAlbumInfo(albumID)
        if (info) {
          changeAlbumInfo({
              title: info.title,
              artist: info.artists_sort,
              picURL: info.thumb
          })
          if (info.labels) {
            label = info.labels[0].name;
          } else label = "Label"
          setArtists(info.artists_sort);
          setTitle(info.title);
          setYear(info.year);
          setLabel(label);
          setPicURL(info.thumb)
        }
      } 
      getInfo()

  },[])

  return (
      <div className="mini-message">
           <img src={picURL} alt="Album Cover"/>
          {toUser && <p>To: {toUser}</p>}
          {fromUser && <p>From: {fromUser}</p>}
          <p>Trading: {album}</p>
          <p>For: {offeredAlbum}</p>
          {message && <p> Message: {message}</p>}

      </div>
  )
}

export default MessageMini
