import { useSelector } from 'react-redux';
import { useState } from 'react';
import MiniAlbum from '../../album-mini/album-mini';
import './new-message.css'

function NewMessage () {
    const toUser = useSelector(state => state.viewedUser.viewedUser)
    const fromUser = useSelector(state => state.user.user)
    const album = useSelector(state => state.eyedAlbum.eyedAlbum)
    const collection = useSelector(state => state.collection.collection)
    const [offeredAlbum, changeOfferedAlbum] = useState('') 

    function handleOffer (album) {
        changeOfferedAlbum(album)
        console.log(offeredAlbum)
    }

    return (
        <div className="message-create">
            <h1>New Message</h1>
            <h2>To : {toUser}</h2>
            <h2>From : {fromUser}</h2>
            <h2>Album:</h2>
            <MiniAlbum albumID={album}/>
            <div className="Collection">
            <h3>Your Collection</h3>
            {collection.map(a => <MiniAlbum key={a} albumID={a} username={fromUser} handleClick={() => handleOffer(a)} clickValue="Offer"/>)}
          </div>
            <div className="search-bar">   
                <form onSubmit={() => {}}>
                    <input
                      type="search"
                      placeholder="Search for album..."
                      name="search"
                      value="wow"
                      onChange={() => {}}
                    />
                    <input type="submit" value="Search" className="button"/>
                </form>
             </div>
        </div>
    )
}

export default NewMessage;
