import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { getTrades } from '../../../../schlepperdb/controller/tradeController';
import apiService from '../../APIservice';
import TradeMini from '../trade-mini/trade-mini';

import './user-trades.css';

const UserTrades = () => {
  
  const user = useSelector(state => state.user.user)
  const [trades, changeTrades] = useState([]);
  
  async function handleClick () {
    await apiService.deleteTrade(tradeInfo.id);
    getTrades()
  }

  useEffect(()=>{
    async function getTrades () {
      const incomingTrades = await apiService.getTrades(user)
      changeTrades(incomingTrades)
    }
    getTrades()
  }, [])

  return(
    <div className="user-trades-container">
        <h1>Active Trades:</h1>
        {
        trades.length && 
        trades.map(t => <TradeMini key={t.id} tradeInfo={t} handleClick={handleClick}/>)
        }
    </div>
    );
  };

export default UserTrades;
