import { authFalse, authTrue, changeUser, changePass } from '../actions';
import { connect } from 'react-redux';
import apiService from '../APIservice';
import React, { useState } from 'react';

const initialState = {
  username: '',
  password: '',
};

function Login ({user, password, changeUser, changePass}) {
  
  const [state, setState] = useState(initialState);
   
  const handleChange = (event) => {
    const { name, value } = event.target;
    setState((state) => {
      return {
      ...state,
      [name]: value,
      }
    });
  };

  async function logAttempt(userObject) {
    const response = await apiService.login(userObject)
    if (response.confirmed){

    }
  } 

  return (
      <div className="container">
          <div className="login">
              <h2>Log In</h2>
              <form onSubmit={(e) => {
                  e.preventDefault();
                  logAttempt(state);
                  setState(initialState);
                  }
              }>
                  <input
                    type="text"
                    placeholder="Username"
                    name="username"
                    value={user.user}
                    onChange={handleChange}
                  />
                  <input
                    type="password"
                    placeholder="Password"
                    name="password"
                    onChange={handleChange}
                  />
                  <input type="submit" value="Log In" className="button"/>
             </form>
          </div>
      </div>
  )
}

//map state
const mapStateToProps = (state) => {
    return {
      auth: state.auth,
      user: state.user,
      password: state.password
    };
  };
  
  // map dispatch
  const mapDispatchToProps = (dispatch) => ({
    authTrue: () => dispatch(authTrue()),
    authFalse: () => dispatch(authFalse()),
    changeUser: (text) => dispatch(changeUser(text)),
    changePass: (text) => dispatch(changePass(text))
  });
  
  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Login);
