import { useEffect, useState } from 'react';
import apiService from '../../APIservice';
import UserTrades from '../user-trades/user-trades';
import './trade-mini.css';

function TradeMini ({tradeInfo}) {
   
  const [album1, changeAlbum1] = useState({});
  const [album2, changeAlbum2] = useState({});

  useEffect(()=>{
    async function getInfo() {
      const info1 = await apiService.getAlbumInfo(tradeInfo.user1offer)
      const info2 = await apiService.getAlbumInfo(tradeInfo.user2offer)
      changeAlbum1(info1);
      changeAlbum2(info2);
    }
    getInfo()
  }, [])


    return (
        <div className="mini">
         <img src={album1.thumb}/>
         <div className="trade-info">
            <div className="user1">
                <p>{tradeInfo.user1}</p>
               <p>{album1.title}</p>
            </div>
            <div className="center">
                <p>{tradeInfo.location}</p>
                <p>{tradeInfo.additional}</p>
            </div>
            <div className="user2">
                <p>{tradeInfo.user2}</p>
                <p>{album2.title}</p>
            </div>
         </div>
         <img src={album2.thumb}/>
        </div>
    )
}

export default TradeMini;
