import { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import MiniAlbum from '../album-mini/album-mini'
import apiService from '../APIservice'
import MessageMini from '../search-minis/message-mini/message-mini'
import './messages.css'

function MessagesTab () {
    const [messages, changeMessages] = useState([])
    const user = useSelector(state => state.user.user)
    
    function handleClick (album) {
        dispatch(changeEyedAlbum(album))
        navigate('/newMessage');
      }

    useEffect(() => {
      async function getMessages () {
          const messageArray =  await apiService.getMessages(user)
          changeMessages(messageArray)
      }
      getMessages()
    },[])


    return (
        <div className="messages-container">
            <div className="incoming">
              <h1>Incoming Messages</h1>
              {messages.map(m => {
                  if (m.toUser === user) return <MessageMini 
                  key={m.message} 
                  fromUser={m.fromUser}
                  message={m.message}
                  album={m.album}
                  offeredAlbum={m.offeredAlbum}
                  handleClick={() => handleClick(m.album)}
                  />
                }
              )}
            </div>
            <div className="outgoing">
              <h1>Sent Messages</h1>
              {messages.map(m => {
                  if (m.fromUser === user) return <MessageMini
                  key={m.message} 
                  toUser={m.toUser}
                  message={m.message}
                  album={m.album}
                  offeredAlbum={m.offeredAlbum}
                  sent={true}
                  />
                }
              )}
            </div>
            
        </div>
    )
    
}

export default MessagesTab
