import {useSelector} from 'react-redux'
import { changeCollection, changeWants } from '../actions'
import MiniAlbum from '../album-mini/album-mini'
import './dashboard.css'

function Dashboard () {
    const user = useSelector(state => state.user.user)
    const collection = useSelector(state => state.collection.collection)
    const wants = useSelector(state => state.wants.wants)

    async function deleteCollection() {
      const newCollection = await apiService.removeCollection({username, albumID});
      dispatch(changeCollection(newCollection));
    }
    
    async function deleteWant() {
      const newWants = await apiService.removeWant({username, albumID});
      dispatch(changeWants(newWants))
    }



    return (
        <div className="dashboard">
          <h2>Hello {user}</h2>
          <div className="Collection">
            <h3>Your Collection</h3>
            {collection.map(a => <MiniAlbum key={a} albumID={a} handleClick={deleteCollection}/>)}
          </div>
          <div className="Wants">
          <h3>Your Wants</h3>
          {wants.map(a => <MiniAlbum key={a} albumID={a} handleClick={deleteWant}/>)}
          </div>
        </div>
    )
}


export default Dashboard
