import { authFalse, authTrue, changeUser } from '../actions';
import { connect } from 'react-redux';


function Login ({user, password, changeUser}) {
    let words = undefined;
    return (
        <div className="container">
            <div className="login">
                <h2>{words? words : 'Log In'}</h2>
                <form onSubmit={(e) => {
                    e.preventDefault();
                    const text = String(e.target[0].value);
                    words = text
                    changeUser(text)
                    console.log(user);
                    }
                }>
                    <input
                      type="text"
                      placeholder="Username"
                      name="username"
                      value={user}
                    />
                    <input
                      type="password"
                      placeholder="Password"
                      name="password"
                      value={password}
                    />
                    <input type="submit" value="Log In" className="button"/>
               </form>
            </div>
        </div>
    )
}

//map state
const mapStateToProps = (state) => {
    return {
      auth: state.auth,
      user: state.user,
      password: state.password
    };
  };
  
  // map dispatch
  const mapDispatchToProps = (dispatch) => ({
    authTrue: () => dispatch(authTrue()),
    authFalse: () => dispatch(authFalse()),
    changeUser: (text) => dispatch(changeUser(text))
  });
  
  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Login);
