import { useState } from 'react';
import './search.css';
import apiService from '../APIservice'

function Search () {
    const [searchQuery, changeSearchQuery] = useState('')
    const [results, changeResults] = useState([])
    
    function handleChange(e){
        const query = e.target.value;
        changeSearchQuery(query)
    }
    
    async function handleSubmit (e) {
       e.preventDefault()
       const res = await apiService.search(searchQuery, 'release')
       if (res){
           changeResults(res.results.slice(0,30))
       }
    }

    return (
        <div className="searchContainer">
            <h1>Search</h1>
            <div className="search-bar">   
                <form onSubmit={handleSubmit}>
                    <input
                      type="search"
                      placeholder="Search for album..."
                      name="search"
                      value={searchQuery}
                      onChange={handleChange}
                    />
                    {results.length && 
                    results.map(r => {
                      return (
                        <div className="mini">
                        <img src={r.thumb} alt="Album Cover"/>
                        <div className="mini-info">
                          <h2>{r.title ? r.title : "Album"}</h2>
                          <p>{r.label[0] ? r.label[0] : "Label"}</p>
                          <p>{r.year ? r.year : "Year"}</p>
                          <button onClick={()=> {}}>Add to Collection</button>
                          <button onClick={()=> {}}>Add to Wants</button>
                        </div>
                      </div>
                      )
                    })
                    }
                    <input type="submit" value="Search" className="button"/>
                </form>
             </div>
        </div>
    )
}

export default Search;
