import { combineReducers } from 'redux';

//redux state
const initialState = {
  auth: false,
  user: null,
};

//reducer to authenticate
const auth = (state = initialState.auth, action) => {
  switch (action.type) {
  case 'AUTH_TRUE' : {
      return {
          ...state,
          auth: true,
      }}

  case 'AUTH_FALSE' : {
    return {
        ...state,
        auth: false,
    }}
  default : return state;
  }
};



// Combining both reducers
const reducers = combineReducers({
  auth,

});

export default reducers;
