import { authTrue, changeUser } from '../actions';

import { connect } from 'react-redux';
import apiService from '../APIservice';
import React, { useState } from 'react';
import { useDispatch } from 'react-redux';

const initialState = {
  username: '',
  password: '',
};

function Login () {
  
  const [state, setState] = useState(initialState);
  
  const handleChange = (event) => {
    const { name, value } = event.target;
    setState((state) => {
      return {
      ...state,
      [name]: value,
      }
    });
  };

  async function logAttempt(userObject) {
    const response = await apiService.login(userObject);
    if (response.confirmed) {
      const {accessToken} = response;
      localStorage.setItem('accessToken', accessToken);
      useDispatch(changeUser(userObject.username));
      useDispatch(authTrue());
    }
  } 

  return (
      <div className="container">
          <div className="login">
              <h2>Log In</h2>
              <form onSubmit={(e) => {
                  e.preventDefault();
                  logAttempt(state);
                  setState(initialState);
                  }
              }>
                  <input
                    type="username"
                    placeholder="Username"
                    name="username"
                    value={state.user}
                    onChange={handleChange}
                  />
                  <input
                    type="password"
                    placeholder="Password"
                    name="password"
                    value={state.password}
                    onChange={handleChange}
                  />
                  <input type="submit" value="Log In" className="button"/>
             </form>
          </div>
      </div>
  )
}

//map state
const mapStateToProps = (state) => {
    return {
      auth: state.auth,
      user: state.user,
    };
  };
  
  // map dispatch
  const mapDispatchToProps = (dispatch) => ({
    authTrue: () => dispatch(authTrue()),
    changeUser: (text) => dispatch(changeUser(text)),
  
  });
  
  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Login);
